// 函数式组件
function Demo(){
  const [count, setCount] = React.useState(0)
  const [name, setName] = React.useState("Tom")

  React.use

  function add(){
    setCount(count => count+1)
  }

  function change(){
    setName('Jack')
  }

  return (
    <div>
      <h2>当前求和为{count}</h2>
      <h2>我的名字是：{name}</h2>
      <button onClick={add}>+</button>
      <button onClick={change}> 点我改名</button>
    </div>
  )
}


export default Demo
